import React, {useEffect, useRef,useMemo} from 'react';
import useSVGCanvas from './useSVGCanvas.js';
import * as d3 from 'd3';


//change the code below to modify the bottom plot view
export default function WhiteHatStats(props){
    const d3Container = useRef(null);
    const [svg, height, width, tTip] = useSVGCanvas(d3Container);

    const margin = { top: 50, right: 20, bottom: 50, left: 40 };
    const barWidth = 200; // Width of each bar

    useEffect(()=>{
        if (!svg || !props.data) return;

        const data = props.data.states;

        const plotData = data.map(state => {
            const dd = drawingDifficulty[state.abreviation];
            return {
                'count': state.count,
                'name': state.state,
                'easeOfDrawing': dd === undefined ? 5 : dd,
                'genderRatio': state.male_count / state.count,
            };
        });

        const xScale = d3.scaleBand()
            .domain(plotData.map(d => d.name))
            .range([margin.left, width - margin.right])
            .padding(0.1);

        const yScale = d3.scaleLinear()
            .domain([0, d3.max(plotData, d => d.count)])
            .nice()
            .range([height - margin.bottom, margin.top]);

        svg.selectAll('.bar').remove();
        svg.selectAll('.bar')
            .data(plotData)
            .enter().append('rect')
            .attr('class', 'bar')
            .attr('x', d => xScale(d.name))
            .attr('y', d => yScale(d.count))
            .attr('width', xScale.bandwidth())
            .attr('height', d => height - margin.bottom - yScale(d.count))
            .attr('fill', 'steelblue')
            .on('mouseover', (e, d) => {
                const string = `${d.name}</br>Gun Deaths: ${d.count}</br>Difficulty Drawing: ${d.easeOfDrawing}`;
                props.ToolTip.moveTTipEvent(tTip, e);
                tTip.html(string);
            })
            .on('mousemove', (e) => {
                props.ToolTip.moveTTipEvent(tTip, e);
            })
            .on('mouseout', () => {
                props.ToolTip.hideTTip(tTip);
            });

        svg.selectAll('.axis').remove();
        svg.append('g')
            .attr('class', 'axis')
            .attr('transform', `translate(0,${height - margin.bottom})`)
            .call(d3.axisBottom(xScale))
            .selectAll('text')
            .attr('dy', '1em')
            .style('text-anchor', 'middle');

        svg.append('g')
            .attr('class', 'axis')
            .attr('transform', `translate(${margin.left},0)`)
            .call(d3.axisLeft(yScale));

        // Change the title
        const labelSize = margin.top / 2;
        svg.selectAll('text').remove();
        svg.append('text')
            .attr('x', width / 2)
            .attr('y', labelSize)
            .attr('text-anchor', 'middle')
            .attr('font-size', labelSize)
            .attr('font-weight', 'bold')
            .text('Gun Deaths by State');

    }, [props.data, svg]);

    return (
        <div
            className={"d3-component"}
            style={{ 'height': '99%', 'width': '99%' }}
            ref={d3Container}
        ></div>
    );
}
